import InnerpageHero from './InnerpageHero';
import img from '../images/img02.jpg'
import Footer from './Footer';

const About = () => {
    return (
        <div className="container mx-auto">
            <InnerpageHero src={img} alt="Hero" heading="About Page" />
            <div>
                <h2>About QuickEat - Delivering Delicious Food to Your Doorstep</h2>
                <p>Welcome to QuickEat, your go-to food delivery app for fast and reliable service! We are a team of passionate foodies and tech enthusiasts dedicated to providing you with the best food delivery experience possible.</p>
                <p>Our goal is to connect you with your favorite restaurants and dishes in just a few clicks, no matter where you are. Whether you're craving Indian, Chinese, Italian, or anything in between, we've got you covered.</p>
                
            </div>
            <Footer />
        </div>
    )
}

export default About;
