import { useState } from "react"
import Footer from "./Footer"

const Login = () => {
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')

  const loginValidation = () => {
    return email.length > 0 && password.length > 0
  }

  const handleSubmit = (event) => {
    event.preventDefault();

  }

  return (
    <div className="container mx-auto">
      <div className='login_page'>
        <form onSubmit={handleSubmit} className="form">
          <div className="form-control">
            <label for="email">Email</label>
            <input type="email" placeholder="Email" value={email} id="email"
              onChange={(event) => setEmail(event.target.value)}
            />
            <small>Error message</small>
          </div>
          <div className="form-control">
            <label for="password">Password</label>
            <input type="password" placeholder="Password" value={password} id="password"
              onChange={(event) => setPassword(event.target.value)}
            />
            <small>Error message</small>
          </div>

          <button className="btn" type="submit" disabled={!loginValidation()}>Login</button>
          <div><a href="signup.html" className="lnk">Don't have an Account</a></div>
        </form>
      </div>
      <Footer />
    </div>
  )
}

export default Login
